--家具的Viewlocal ViewFurniture = class("ViewFurniture",require("app.object.view.ViewObject"))local LuaUtility = CS.Game.LuaUtilityfunction ViewFurniture:ctor(layer)    self.layer = layerendfunction ViewFurniture:Load(res, cb, caller)    self:DoLoad(res, LuaUtility.LoadFurnitureItem, cb, caller,self.layer)endfunction ViewFurniture:Locate(dir,posIndex)    LuaUtility.LocFurnitureItem(self.view, dir, posIndex)end--放入PlaceManger格子中function ViewFurniture:PlaceItem()    LuaUtility.AddPlaceItem(self.view)endfunction ViewFurniture:SetViewModel(viewModel)    self.view = viewModelendreturn ViewFurniture