local ViewDeleteBuild =class("ViewDeleteBuild",require("app.ui.UiView"))ViewDeleteBuild.res = "deletebuild"local LuaUtility = CS.Game.LuaUtilityfunction ViewDeleteBuild:OnOpen()    self:RegisterButtonClick(self.deleteBtn,self.OnDeleteClick)    self:RegisterButtonClick(self.recoverBtn,self.OnRecoverClick)endfunction ViewDeleteBuild:OnClose()    self:UnregisterButtonClick(self.recoverBtn)    self:UnregisterButtonClick(self.deleteBtn)endfunction ViewDeleteBuild:OnDeleteClick()    LuaUtility.DeleteSelectBuilding()    self:Close()endfunction ViewDeleteBuild:OnRecoverClick()    LuaUtility.RecoverSelectBuilding()    self:Close()end_G.ViewDeleteBuild = ViewDeleteBuild